<?phpclass testEmValidate extends TestInterface{	public function test()	{		$this->assertClassHasAttribute('data', 'Validate');		$this->assertArrayHasKey('state', $data);	}	public function testValidte()	{		$this->assertNotNull($data['process']);		if($this->assertEqulas('delete', $data['process']) || $this->assertEquals('selectById', $data['process']))		{			$this->assertTrue($this->testValid_int($data['id'], 1,11));			$em = new Employee();			$result = $em->validate();			$this->assertEquals(1, $result);		}		if($this->assertEquals('insert', $data['process']))		{			$this->assertNull($data['id']);			$this->assertTrue($this->testValid_string($data['name'], 1,100));			$this->assertTrue($this->testValid_string($data['title'], 1, 100));			$em = new Employee();			$result = $em->validate();			$this->assertEquals(1, $result);		}		if($this->assertEquals('update', $data['process']))		{			$this->assertTrue($this->testValid_int($data['id'],1,11));			$this->assertTrue($this->testValid_string($data['name'], 1,100));			$this->assertTrue($this->testValid_string($data['title'], 1, 100));			$em = new Employee();			$result = $em->validate();			$this->assertEquals(1, $result);		}	}}